/** * JavaUtilTimerEx.java * Example of how to use java.util.Timer * @author Pete Lutz * @version 10-24-2017 */public class JavaUtilTimerEx {   public static void main(String[] args) {      java.util.Timer timer = new java.util.Timer();         java.util.TimerTask task =          new java.util.TimerTask() {            private int runNo = 1;            public void run() {               System.out.println("Timer run #" + runNo);               runNo ++;            }         };         timer.scheduleAtFixedRate(task, 0, 1000);   }}